// ============================================================
// app.js — Script unique (fusion des modules ES)
// Ordre : data → physics → scene → interaction → main
// ============================================================

(function () {

// ============================================================
// 1. DATA — Données des 4 dimensions du rétablissement
// Basé sur van der Stel (2012) adapté par Franck (2018)
// ============================================================

const dimensionsData = [
    {
        id: 0,
        title: "Rétablissement Social",
        shortDesc: "Logement, emploi, liens sociaux et intégration citoyenne",
        desc: "Implique l'autonomie en termes de logement, d'emploi, de revenu et la richesse des relations sociales. Cela reflète l'intégration citoyenne.",
        color: 0x3b82f6,
        cause: "La perte des liens sociaux et de l'autonomie matérielle isole l'individu. Sans ancrage dans la communauté, l'exclusion sociale s'installe, entraînant la chute des autres piliers."
    },
    {
        id: 1,
        title: "Rétablissement Clinique",
        shortDesc: "Rémission symptomatique et gestion de la pathologie",
        desc: "Concerne la rémission symptomatique via des interventions psychologiques et pharmacologiques. C'est la gestion de la pathologie.",
        color: 0xef4444,
        cause: "Sans une gestion adéquate et apaisée des symptômes, la souffrance psychique et cognitive sature l'espace mental, rendant le fonctionnement quotidien et social impraticables."
    },
    {
        id: 2,
        title: "Rétablissement Fonctionnel",
        shortDesc: "Capacité à affronter le quotidien et gérer ses limitations",
        desc: "Restauration de la capacité à affronter des situations quotidiennes, à utiliser ses capacités et à compenser ou gérer ses limitations.",
        color: 0xeab308,
        cause: "L'incapacité à accomplir les activités de la vie quotidienne crée une dépendance invalidante. Cette perte de capacité entrave directement l'espoir personnel et l'intégration sociale."
    },
    {
        id: 3,
        title: "Rétablissement Personnel",
        shortDesc: "Espoir, identité, sens de l'existence et empowerment",
        desc: "Processus intime de restauration du sens de l'existence, de l'identité, de l'espoir et du pouvoir d'agir (empowerment).",
        color: 0x14b8a6,
        cause: "L'absence d'espoir, de sens et d'estime de soi détruit la motivation intrinsèque. Sans ce moteur intime, les traitements cliniques et les aides sociales perdent leur utilité subjective."
    }
];

const originalPositions = [
    { x:  2.425, y: 1.2, z: -1.4 },  // 0: Social (arrière-droite)
    { x: -2.425, y: 1.2, z: -1.4 },  // 1: Clinique (arrière-gauche)
    { x:  0,     y: 1.2, z:  2.8 },  // 2: Fonctionnel (avant-centre)
    { x:  0,     y: 1.2, z:  0   }   // 3: Personnel (CENTRE)
];

const collapseStates = [
    { rx:  0.4, rz:  0.4, slideX: -1, slideZ:  1 },                  // 0 retiré → bascule avant-gauche (côté lourd)
    { rx:  0.4, rz: -0.4, slideX:  1, slideZ:  1 },                  // 1 retiré → bascule avant-droite (côté lourd)
    { rx: -0.5, rz:  0,   slideX:  0, slideZ: -1 },                   // 2 retiré → bascule arrière (côté lourd)
    { rx:  0.7, rz:  0.7, slideX:  0, slideZ:  0, chaotic: true }     // 3 retiré → chaotique (centre)
];

// Texture bois réelle (ambientCG Wood048, CC0)
var WOOD_TEXTURE_B64 = 'data:image/jpeg;base64,';


// ============================================================
// 2. PHYSICS — Logique d'effondrement et de restauration
// ============================================================

function triggerCollapse(index, state) {
    state.isCollapsed = true;
    state.collapsedIndex = index;
    state.collapseStartTime = Date.now();

    // Phase de levée : la sphère monte avant que le plateau ne bascule
    state.liftPhase = true;
}

function resetSystem(state) {
    // Réinitialiser le plateau
    state.boardGroup.rotation.set(0, 0, 0);

    // Réinitialiser chaque sphère
    state.spheres.forEach(function (sphere, i) {
        sphere.visible = true;
        var pos = originalPositions[i];
        sphere.position.set(pos.x, pos.y, pos.z);
        sphere.rotation.set(0, 0, 0);
        state.velocities[i].set(0, 0, 0);
    });

    state.isCollapsed = false;
    state.collapsedIndex = -1;
    state.selectedSphereIndex = -1;
    state.liftPhase = false;

    setTimeout(function () {
        state.isInteractive = true;
    }, 400);
}

function updatePhysics(state) {
    // --- Phase de levée : la sphère retirée monte vers le haut ---
    if (state.liftPhase && state.collapsedIndex !== -1) {
        var sphere = state.spheres[state.collapsedIndex];
        sphere.position.y += 0.15;

        // Une fois assez haute, la cacher
        if (sphere.position.y > 6) {
            sphere.visible = false;
            state.liftPhase = false;
        }
        // PAS de return → le basculement commence en parallèle
    }

    // --- Phase d'effondrement (commence dès le retrait) ---
    if (state.isCollapsed && state.collapsedIndex !== -1) {
        var cs = collapseStates[state.collapsedIndex];

        if (cs.chaotic) {
            // Effondrement chaotique (Personnel retiré = centre)
            var now = Date.now();
            var elapsed = now - state.collapseStartTime;
            var t = now * 0.002;

            // Amplitude qui monte puis redescend (amortissement exponentiel)
            var ramp = Math.min(elapsed * 0.001, 1.0);       // montée 0→1 en ~1s
            var damping = Math.exp(-elapsed * 0.0008);        // décroissance exponentielle
            var amplitude = ramp * damping;                    // monte puis s'éteint

            // Oscillation amortie + convergence vers position inclinée fixe
            var targetRx = Math.sin(t * 3.7) * cs.rx * amplitude + cs.rx * 0.3 * (1 - damping);
            var targetRz = Math.cos(t * 2.3) * cs.rz * amplitude + cs.rz * 0.3 * (1 - damping);
            state.boardGroup.rotation.x += (targetRx - state.boardGroup.rotation.x) * 0.05;
            state.boardGroup.rotation.z += (targetRz - state.boardGroup.rotation.z) * 0.05;

            // Sphères poussées radialement vers l'extérieur
            state.spheres.forEach(function (sphere, i) {
                if (i !== state.collapsedIndex && sphere.visible) {
                    var dx = sphere.position.x;
                    var dz = sphere.position.z;
                    var dist = Math.sqrt(dx * dx + dz * dz) || 1;

                    // Poussée radiale croissante
                    state.velocities[i].x += (dx / dist) * 0.004 * amplitude;
                    state.velocities[i].z += (dz / dist) * 0.004 * amplitude;

                    var distFromCenter = Math.sqrt(
                        sphere.position.x * sphere.position.x + sphere.position.z * sphere.position.z
                    );

                    if (distFromCenter > 4.5) {
                        state.velocities[i].y -= 0.015;
                        sphere.rotation.x += 0.1;
                        sphere.rotation.z += 0.1;
                    }

                    sphere.position.add(state.velocities[i]);

                    if (sphere.position.y < -20) {
                        sphere.visible = false;
                    }
                }
            });
        } else {
            // Effondrement directionnel standard
            // Basculement progressif du plateau (lerp)
            state.boardGroup.rotation.x += (cs.rx - state.boardGroup.rotation.x) * 0.03;
            state.boardGroup.rotation.z += (cs.rz - state.boardGroup.rotation.z) * 0.03;

            // Glissement et chute des sphères restantes
            state.spheres.forEach(function (sphere, i) {
                if (i !== state.collapsedIndex && sphere.visible) {
                    state.velocities[i].x += cs.slideX * 0.003;
                    state.velocities[i].z += cs.slideZ * 0.003;

                    var distFromCenter = Math.sqrt(
                        sphere.position.x * sphere.position.x + sphere.position.z * sphere.position.z
                    );

                    if (distFromCenter > 4.5) {
                        state.velocities[i].y -= 0.015;
                        sphere.rotation.x += 0.1;
                        sphere.rotation.z += 0.1;
                    }

                    sphere.position.add(state.velocities[i]);

                    if (sphere.position.y < -20) {
                        sphere.visible = false;
                    }
                }
            });
        }
    } else if (!state.isCollapsed && state.isInteractive) {
        // Léger balancement naturel (respiration)
        var time = Date.now() * 0.001;
        state.boardGroup.rotation.x = Math.sin(time) * 0.02;
        state.boardGroup.rotation.z = Math.cos(time * 0.8) * 0.02;
    }
}

// ============================================================
// 3. SCENE — Setup Three.js : scène, caméra, lumières, objets
// ============================================================

var labelNames = ['Social', 'Clinique', 'Fonctionnel', 'Personnel'];

function createScene(container) {
    var scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f4f8);
    scene.fog = new THREE.Fog(0xf0f4f8, 10, 40);

    var camera = new THREE.PerspectiveCamera(
        45,
        window.innerWidth / window.innerHeight,
        0.1,
        100
    );
    // Caméra adaptative selon la taille d'écran
    if (window.innerWidth < 480) {
        camera.position.set(0, 16, 24);
    } else if (window.innerWidth < 768) {
        camera.position.set(0, 14, 20);
    } else {
        camera.position.set(0, 10, 15);
    }

    var renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    container.appendChild(renderer.domElement);

    var controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.minDistance = 8;
    controls.maxDistance = 25;
    controls.maxPolarAngle = Math.PI / 2 - 0.1;

    // Empêcher le scroll/zoom tactile par défaut sur le canvas
    renderer.domElement.style.touchAction = 'none';

    return { scene: scene, camera: camera, renderer: renderer, controls: controls };
}

function createLights(scene) {
    var ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambientLight);

    var dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight.position.set(10, 15, 10);
    dirLight.castShadow = true;
    dirLight.shadow.mapSize.width = 2048;
    dirLight.shadow.mapSize.height = 2048;
    dirLight.shadow.camera.near = 0.5;
    dirLight.shadow.camera.far = 50;
    dirLight.shadow.camera.left = -10;
    dirLight.shadow.camera.right = 10;
    dirLight.shadow.camera.top = 10;
    dirLight.shadow.camera.bottom = -10;
    dirLight.shadow.bias = -0.001;
    scene.add(dirLight);
}

// --- Labels billboard (THREE.Sprite + Canvas) ---

function roundRect(ctx, x, y, w, h, r) {
    ctx.beginPath();
    ctx.moveTo(x + r, y);
    ctx.lineTo(x + w - r, y);
    ctx.quadraticCurveTo(x + w, y, x + w, y + r);
    ctx.lineTo(x + w, y + h - r);
    ctx.quadraticCurveTo(x + w, y + h, x + w - r, y + h);
    ctx.lineTo(x + r, y + h);
    ctx.quadraticCurveTo(x, y + h, x, y + h - r);
    ctx.lineTo(x, y + r);
    ctx.quadraticCurveTo(x, y, x + r, y);
    ctx.closePath();
}

function createSphereLabel(text) {
    var canvas = document.createElement('canvas');
    var ctx = canvas.getContext('2d');

    canvas.width = 512;
    canvas.height = 128;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Mesurer le texte pour dimensionner la pilule
    ctx.font = 'bold 48px "Segoe UI", Arial, sans-serif';
    var metrics = ctx.measureText(text);
    var textWidth = metrics.width;
    var padding = 28;

    // Pilule arrondie noire semi-transparente
    var bgW = textWidth + padding * 2;
    var bgH = 72;
    var bgX = (canvas.width - bgW) / 2;
    var bgY = (canvas.height - bgH) / 2;

    ctx.fillStyle = 'rgba(0, 0, 0, 0.5)';
    roundRect(ctx, bgX, bgY, bgW, bgH, 20);
    ctx.fill();

    // Texte blanc
    ctx.fillStyle = '#ffffff';
    ctx.font = 'bold 48px "Segoe UI", Arial, sans-serif';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(text, canvas.width / 2, canvas.height / 2);

    var texture = new THREE.CanvasTexture(canvas);
    texture.needsUpdate = true;

    var spriteMaterial = new THREE.SpriteMaterial({
        map: texture,
        transparent: true,
        depthTest: false,
        depthWrite: false
    });

    var sprite = new THREE.Sprite(spriteMaterial);
    sprite.scale.set(3.5, 0.875, 1); // ratio 4:1 du canvas

    return sprite;
}

// --- Texture "Rétablissement" gravée sur le plateau ---

function createBoardTopTexture(woodImg) {
    var canvas = document.createElement('canvas');
    var ctx = canvas.getContext('2d');

    canvas.width = 1024;
    canvas.height = 1024;

    // Texture bois photographique
    ctx.drawImage(woodImg, 0, 0, 1024, 1024);

    // Texte "Rétablissement" × 4, effet pyrographie (gravure au feu)
    ctx.font = 'bold 58px Georgia, "Times New Roman", serif';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';

    for (var i = 0; i < 4; i++) {
        ctx.save();
        ctx.translate(512, 512);
        ctx.rotate(Math.PI + i * Math.PI / 2);

        // Passe 1 : Halo de chaleur
        ctx.shadowColor = 'rgba(80, 40, 0, 0.6)';
        ctx.shadowBlur = 12;
        ctx.shadowOffsetX = 0;
        ctx.shadowOffsetY = 0;
        ctx.fillStyle = 'rgba(30, 15, 0, 0.7)';
        ctx.fillText('Rétablissement', 0, 320);

        // Passe 2 : Texte carbonisé + micro-profondeur
        ctx.shadowColor = 'rgba(0, 0, 0, 0.3)';
        ctx.shadowBlur = 2;
        ctx.shadowOffsetX = 0;
        ctx.shadowOffsetY = 1;
        ctx.fillStyle = 'rgba(20, 8, 0, 0.85)';
        ctx.fillText('Rétablissement', 0, 320);

        // Passe 3 : Reflet lumineux (bord supérieur)
        ctx.shadowColor = 'transparent';
        ctx.shadowBlur = 0;
        ctx.fillStyle = 'rgba(180, 140, 80, 0.12)';
        ctx.fillText('Rétablissement', 0, 319);

        ctx.restore();
    }

    var texture = new THREE.CanvasTexture(canvas);
    texture.needsUpdate = true;
    return texture;
}

function createSceneObjects(scene, woodImg) {
    var woodMaterial = new THREE.MeshStandardMaterial({
        color: 0xdeb887,
        roughness: 0.8,
        metalness: 0.1
    });

    // Sol pour recevoir les ombres
    var floorGeo = new THREE.PlaneGeometry(100, 100);
    var floorMat = new THREE.ShadowMaterial({ opacity: 0.1 });
    var floor = new THREE.Mesh(floorGeo, floorMat);
    floor.rotation.x = -Math.PI / 2;
    floor.position.y = -2;
    floor.receiveShadow = true;
    scene.add(floor);

    // Groupe du plateau
    var boardGroup = new THREE.Group();
    scene.add(boardGroup);

    // Plateau avec texture gravée sur le dessus
    var topTexture = createBoardTopTexture(woodImg);
    var topMaterial = new THREE.MeshStandardMaterial({
        map: topTexture,
        roughness: 0.8,
        metalness: 0.1
    });
    var boardMaterials = [woodMaterial, topMaterial, woodMaterial]; // côté, dessus, dessous

    var boardGeo = new THREE.CylinderGeometry(5, 5, 0.4, 64);
    var board = new THREE.Mesh(boardGeo, boardMaterials);
    board.castShadow = true;
    board.receiveShadow = true;
    boardGroup.add(board);

    // Les 4 sphères
    var spheres = [];
    dimensionsData.forEach(function (data, index) {
        var mat = new THREE.MeshStandardMaterial({
            color: data.color,
            roughness: 0.3,
            metalness: 0.2
        });
        var sphere = new THREE.Mesh(new THREE.SphereGeometry(1, 32, 32), mat);

        var pos = originalPositions[index];
        sphere.position.set(pos.x, pos.y, pos.z);
        sphere.castShadow = true;
        sphere.receiveShadow = true;
        sphere.userData = data;

        boardGroup.add(sphere);
        spheres.push(sphere);
    });

    // Labels billboard au-dessus de chaque sphère
    var labelSprites = [];
    spheres.forEach(function (sphere, index) {
        var label = createSphereLabel(labelNames[index]);
        // Position initiale (sera mise à jour dans animate)
        var worldPos = new THREE.Vector3();
        sphere.getWorldPosition(worldPos);
        label.position.set(worldPos.x, worldPos.y + 1.6, worldPos.z);
        scene.add(label); // ajouté à la scène, pas au boardGroup
        labelSprites.push(label);
    });

    return { boardGroup: boardGroup, spheres: spheres, labelSprites: labelSprites };
}

// ============================================================
// 4. INTERACTION — Raycasting, hover, tooltip, clic, modales
// ============================================================

var raycaster;
var mouse;
var isTouchDevice = ('ontouchstart' in window) || (navigator.maxTouchPoints > 0);

function setupInteraction(camera, state) {
    raycaster = new THREE.Raycaster();
    mouse = new THREE.Vector2();

    window.addEventListener('resize', function () { onWindowResize(camera, state.renderer); });
    window.addEventListener('pointermove', function (e) { onPointerMove(e, camera, state); });
    window.addEventListener('pointerdown', function (e) { onPointerDown(e, camera, state); });

    // Boutons des modales
    document.getElementById('btn-close-modal').addEventListener('click', function () { closeModal(state); });
    document.getElementById('btn-remove-pillar').addEventListener('click', function () { removePillar(state); });
    document.getElementById('btn-restore').addEventListener('click', function () { handleReset(state); });

    // Footer toggle
    var footerToggle = document.getElementById('footer-toggle');
    var footerPanel = document.getElementById('footer-panel');
    var footerClose = document.getElementById('footer-close');

    footerToggle.addEventListener('click', function () {
        footerPanel.classList.add('open');
    });
    footerClose.addEventListener('click', function () {
        footerPanel.classList.remove('open');
    });
}

function onWindowResize(camera, renderer) {
    camera.aspect = window.innerWidth / window.innerHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(window.innerWidth, window.innerHeight);

    // Caméra adaptative selon la taille d'écran
    if (window.innerWidth < 480) {
        camera.position.set(0, 16, 24);
    } else if (window.innerWidth < 768) {
        camera.position.set(0, 14, 20);
    } else {
        camera.position.set(0, 10, 15);
    }
}

function onPointerMove(event, camera, state) {
    if (!state.isInteractive || state.isCollapsed) {
        document.body.style.cursor = 'default';
        hideTooltip();
        return;
    }

    mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
    mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

    raycaster.setFromCamera(mouse, camera);
    var intersects = raycaster.intersectObjects(state.spheres);

    if (intersects.length > 0) {
        document.body.style.cursor = 'pointer';
        var hitSphere = intersects[0].object;
        hitSphere.scale.set(1.05, 1.05, 1.05);

        // Tooltip au survol (pas sur tactile)
        if (!isTouchDevice) {
            showTooltip(hitSphere.userData, event.clientX, event.clientY);
        }
    } else {
        document.body.style.cursor = 'default';
        state.spheres.forEach(function (s) { s.scale.set(1, 1, 1); });
        hideTooltip();
    }
}

function onPointerDown(event, camera, state) {
    if (!state.isInteractive || state.isCollapsed) return;

    mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
    mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

    raycaster.setFromCamera(mouse, camera);
    var intersects = raycaster.intersectObjects(state.spheres);

    if (intersects.length > 0) {
        var clickedObject = intersects[0].object;
        openModal(clickedObject.userData, state);
    }
}

// --- Tooltip ---

function showTooltip(data, mouseX, mouseY) {
    var tooltip = document.getElementById('hover-tooltip');
    var tooltipTitle = document.getElementById('tooltip-title');
    var tooltipDesc = document.getElementById('tooltip-desc');
    var tooltipDot = document.getElementById('tooltip-dot');

    tooltipTitle.innerText = data.title;
    tooltipDesc.innerText = data.shortDesc || data.desc;

    var colorHex = '#' + data.color.toString(16).padStart(6, '0');
    tooltipDot.style.backgroundColor = colorHex;

    // Positionner près du curseur
    var offsetX = 16;
    var offsetY = 16;
    var left = mouseX + offsetX;
    var top = mouseY + offsetY;

    // Éviter le débordement à droite
    if (left + 260 > window.innerWidth) {
        left = mouseX - 260 - offsetX;
    }
    // Éviter le débordement en bas
    if (top + 100 > window.innerHeight) {
        top = mouseY - 100 - offsetY;
    }
    // Éviter le débordement à gauche
    if (left < 8) {
        left = 8;
    }

    tooltip.style.left = left + 'px';
    tooltip.style.top = top + 'px';
    tooltip.classList.remove('hidden');
}

function hideTooltip() {
    var tooltip = document.getElementById('hover-tooltip');
    if (tooltip) {
        tooltip.classList.add('hidden');
    }
}

// --- Modales ---

function openModal(data, state) {
    hideTooltip();
    state.isInteractive = false;
    state.selectedSphereIndex = data.id;

    var modal = document.getElementById('info-modal');
    document.getElementById('modal-title').innerText = data.title;
    document.getElementById('modal-desc').innerText = data.desc;

    var colorHex = '#' + data.color.toString(16).padStart(6, '0');
    document.getElementById('modal-color-indicator').style.backgroundColor = colorHex;

    modal.classList.remove('hidden', 'modal-exit-active');
    modal.classList.add('modal-enter');
    void modal.offsetWidth; // force reflow
    modal.classList.add('modal-enter-active');
}

function closeModal(state) {
    var modal = document.getElementById('info-modal');
    modal.classList.remove('modal-enter', 'modal-enter-active');
    modal.classList.add('modal-exit-active');

    setTimeout(function () {
        modal.classList.add('hidden');
        modal.classList.remove('modal-exit-active');
        if (!state.isCollapsed) state.isInteractive = true;
    }, 300);
}

function removePillar(state) {
    closeModal(state);

    var index = state.selectedSphereIndex;
    triggerCollapse(index, state);

    // Afficher la modale d'effondrement après un délai
    var causeText = dimensionsData[index].cause;
    document.getElementById('collapse-reason').innerText = causeText;

    setTimeout(function () {
        var collapseModal = document.getElementById('collapse-modal');
        collapseModal.classList.remove('hidden', 'collapse-exit-active');
        collapseModal.classList.add('collapse-enter');
        void collapseModal.offsetWidth;
        collapseModal.classList.add('collapse-enter-active');
    }, 2500);
}

function handleReset(state) {
    // Cacher la modale d'effondrement
    var collapseModal = document.getElementById('collapse-modal');
    collapseModal.classList.remove('collapse-enter', 'collapse-enter-active');
    collapseModal.classList.add('collapse-exit-active');
    setTimeout(function () { collapseModal.classList.add('hidden'); }, 300);

    resetSystem(state);
}

// ============================================================
// 5. MAIN — Point d'entrée, initialisation, boucle de rendu
// ============================================================

// État global partagé entre les modules
var state = {
    scene: null,
    camera: null,
    renderer: null,
    controls: null,
    boardGroup: null,
    spheres: [],
    labelSprites: [],
    velocities: [
        new THREE.Vector3(),
        new THREE.Vector3(),
        new THREE.Vector3(),
        new THREE.Vector3()
    ],
    isInteractive: true,
    selectedSphereIndex: -1,
    isCollapsed: false,
    collapsedIndex: -1,
    liftPhase: false,
    collapseStartTime: 0
};

var _worldPos = new THREE.Vector3();

function init() {
    // Charger la texture bois avant d'initialiser la scène
    var woodImg = new Image();
    woodImg.onload = function () {
        initScene(woodImg);
    };
    woodImg.src = WOOD_TEXTURE_B64;
}

function initScene(woodImg) {
    var container = document.getElementById('canvas-container');

    // Setup scène
    var sceneSetup = createScene(container);
    state.scene = sceneSetup.scene;
    state.camera = sceneSetup.camera;
    state.renderer = sceneSetup.renderer;
    state.controls = sceneSetup.controls;

    // Lumières
    createLights(state.scene);

    // Objets 3D
    var objects = createSceneObjects(state.scene, woodImg);
    state.boardGroup = objects.boardGroup;
    state.spheres = objects.spheres;
    state.labelSprites = objects.labelSprites;

    // Interactions
    setupInteraction(state.camera, state);

    // Démarrer la boucle
    animate();
}

function animate() {
    requestAnimationFrame(animate);
    state.controls.update();
    updatePhysics(state);

    // Mise à jour des labels billboard (suivi des sphères)
    state.spheres.forEach(function (sphere, i) {
        var label = state.labelSprites[i];
        if (label) {
            label.visible = sphere.visible;
            if (sphere.visible) {
                sphere.getWorldPosition(_worldPos);
                label.position.set(_worldPos.x, _worldPos.y + 1.6, _worldPos.z);
            }
        }
    });

    state.renderer.render(state.scene, state.camera);
}

window.onload = init;

})();
